tinymce.PluginManager.add("sxmfilemgr",function(editor, url) 
{
        function loadSxmFileManager() 
        {
            editor.windowManager.open(
            {
                    title:"Менеджер файлов",
                    url: url+"/dialog.htm",
                    width:600,
                    height:400,
                    buttons:[{text:"Close",onclick:"close"}]
            })
        };
	editor.addButton("sxmfilemgr",
	{
		text:"Менеждер файлов",
                icon: 'file',
		image: url + '/img/btnico.png',
		onclick:function()
		{
                    loadSxmFileManager()
		}
	}),
	editor.addMenuItem("sxmfilemgr",
	{
		text:"Менеджер файлов",
		context:"tools",
		onclick:function()
		{
                    loadSxmFileManager()
		}
	})
});
